mapper.insert:
insert into w_cat_data (cat_dataid, cat_catid, cat_dataname
  )
values (#{catDataid,jdbcType=INTEGER}, #{catCatid,jdbcType=INTEGER}, #{catDataname,jdbcType=VARCHAR}
  )

mapper.update:
update w_cat_data
set cat_catid = #{catCatid,jdbcType=INTEGER},
  cat_dataname = #{catDataname,jdbcType=VARCHAR}
where cat_dataid = #{catDataid,jdbcType=INTEGER}

mapper.select:
select cat_dataid, cat_catid, cat_dataname
from w_cat_data
where cat_dataid = #{catDataid,jdbcType=INTEGER}
