mapper.update:
update w_check
set check_user_id = #{checkUserId,jdbcType=INTEGER},
  check_time = #{checkTime,jdbcType=DATE},
  check_day = #{checkDay,jdbcType=INTEGER}
where check_id = #{checkId,jdbcType=INTEGER}

mapper.select:
select check_id, check_user_id, check_time, check_day
from w_check
where check_id = #{checkId,jdbcType=INTEGER}
