mapper.update:
update w_collect
set collect_userid = #{collectUserid,jdbcType=INTEGER},
  collect_spuid = #{collectSpuid,jdbcType=INTEGER},
  collect_time = #{collectTime,jdbcType=TIMESTAMP}
where collect_id = #{collectId,jdbcType=INTEGER}

mapper.select:
select collect_id, collect_userid, collect_spuid, collect_time
from w_collect
where collect_id = #{collectId,jdbcType=INTEGER}
