mapper.update:
update w_integral
set integral_user_id = #{integralUserId,jdbcType=INTEGER},
  integral_source = #{integralSource,jdbcType=INTEGER},
  integral_order_id = #{integralOrderId,jdbcType=INTEGER}
where integral_id = #{integralId,jdbcType=INTEGER}

mapper.select:
select integral_id, integral_user_id, integral_source, integral_order_id
from w_integral
where integral_id = #{integralId,jdbcType=INTEGER}
