mapper.update:
update w_shopcart
set shopcart_user_id = #{shopcartUserId,jdbcType=INTEGER},
  shopcart_spu_id = #{shopcartSpuId,jdbcType=INTEGER},
  shopcart_shop_number = #{shopcartShopNumber,jdbcType=INTEGER}
where shopcart_id = #{shopcartId,jdbcType=INTEGER}

mapper.select:
select shopcart_id, shopcart_user_id, shopcart_spu_id, shopcart_shop_number
from w_shopcart
where shopcart_id = #{shopcartId,jdbcType=INTEGER}
